import './Must.css';
import React from 'react';
import back from './img/back.png';
import { BrowserRouter as Router, Link } from 'react-router-dom';
import { Routes, Route, useLocation } from 'react-router-dom';
import { RemainTitle,MustInfoContainer,Mustinfo } from './components/Style';

const Must = () => {
  function MustComponent() {
    const location = useLocation();
    const RCResult = location.state?.RCResult || []; // 獲取查詢結果

    return (
      <div className="Must">
        <div className='must_bg'>
          <div>
            <Link to='/Search'>
              <img src={back} alt="回上一頁" className="must_backicon" />
            </Link>
          </div>
          <div className="must_title">
            <label className="titleText">必選修課程</label>
          </div>
          <div className='mustLable'>
           <MustInfoContainer>
            {RCResult.map((item, index) => (
                 <React.Fragment key={item.id}>
                   <Mustinfo>
                     {item.cname}&emsp;{item.cnumber}&emsp;{item.cgrade}
                   </Mustinfo>
                   {index !== RCResult.length - 1 && <br />} {/* 添加斷行，除了最後一項之外的其他項後面都加上斷行 */}
                 </React.Fragment>
               ))}
             </MustInfoContainer>
          </div>
        </div>
      </div>
    );
  }

  return (
    <Routes>
      <Route path="/" element={<MustComponent />} />
    </Routes>
  );
}

export default Must;
