                  <PostArticleBtn>我要發文</PostArticleBtn>
                </Link>
                <ArticleList>
                    <Articleinfo author={"evelyn"} text={"球球排球課"}></Articleinfo>
                    <Articleinfo author={"vvvvvvvvv"} text={"我要涼課"}>hiiiii</Articleinfo>
                </ArticleList>
            </Pagebg>
        </Page>
      );
    }

    return (
           
                <Routes>
                    <Route path="/" element={<ChangeClass />} />
                </Routes>
             
    );
}

export default ChangeClass;
